import React, { Component } from "react";


import "../pages/Home.css";
import Search from '../component/Search';
import SearchBar from '../component/SearchBar';
import Dropdown from '../component/Dropdown';

class Home extends Component {
  // Create a default state of this component
  constructor() {
    super();
    this.state = {
      courses: []
    };
  }

  componentDidMount() {
    fetch('http://localhost:8080/courses')
    .then(response => {
      if (response.ok) { return response.json(); } 
      else { return Promise.reject(response); }
    })
    .then(courseList => this.setState({ courses: courseList}))
  }

  searchedCourse(course) {
    return true;    
  }

  greet() {
    console.log("hello world");
  }
  
  render() {
    let filtCourses = this.state.courses.filter(this.searchedCourse);
    console.log(filtCourses);
    return (
      <div className="Home">
        <h1 class = "pad">Scheduler Application </h1>
        <Dropdown/>
        <Search />
        <SearchBar />
            <table>
              <thead>
                <tr>
                  <td>Term</td>
                  <td>Code</td>
                  <td>Section</td>
                  <td>Title</td>
                  <td>Professor</td>
                  <td>Credits</td>
                </tr>
              </thead>
              <tbody>
                {filtCourses.map(course => {
                  return (
                  <tr>
                    <td><button onClick={ this.greet }>Add</button></td>
                    <td>{(course.term == "F20") ? "Fall" : "Spring"}</td>
                    <td>{course.code.split(" ").slice(0,2).join(" ")}</td>
                    <td>{course.code.split(" ")[2]}</td>
                    <td>{course.title}</td>
                    <td>{course.name}</td>
                    <td>{course.credits}</td>
                  </tr>
                  )
                })}
              </tbody>
            </table>
      </div>
    );
  }
}

export default Home;
