import React, { Component } from "react";


import "../pages/Home.css";
import Search from '../component/Search';
import SearchBar from '../component/SearchBar';
import Dropdown from '../component/Dropdown';

const courses = [
    { id: '12345', name: 'Course One' },
    { id: '22345', name: 'Course Two' },
    { id: '334536', name: 'Course Three' },
    { id: '42345', name: 'Course Four' },
];

function fetchData(ext, save) {
  console.log("fetching http://localhost:8080/" + ext)
  fetch("http://localhost:8080/" + ext)
  .then(response => {
    if (response.ok) {
      save = response.json();
      console.log(save)
    }
  })
}


class Home extends Component {
  // Create a default state of this component
  constructor() {
    super();
    this.state = {};
    this.state.courses = [];
    this.state.fallCourses = [];
    this.state.springCourses = [];
    fetchData("courses", this.state.courses)
    fetchData("fall", this.state.fallCourses)
    fetchData("spring", this.state.springCourses)
  }
  render() {
    setTimeout(console.log(this.state), 100)
    return (
      <div className="Home">
        <h1 class = "pad">Scheduler Application </h1>
        <Dropdown/>
        <Search />
        <SearchBar />
          <ul>
            
          </ul>
      </div>
    );
  }
}

export default Home;
