import React, { Component } from "react";


import "../pages/Home.css";
import Search from '../component/Search';
import SearchBar from '../component/SearchBar';
import Dropdown from '../component/Dropdown';

const courses = [
    { id: '12345', name: 'Course One' },
    { id: '22345', name: 'Course Two' },
    { id: '334536', name: 'Course Three' },
    { id: '42345', name: 'Course Four' },
];

class Home extends Component {
  // Create a default state of this component
  constructor() {
    super();
    this.state = {
      courses: []
    };
  }

  componentDidMount() {
    fetch('http://localhost8080/courses')
    .then(response => {
      if (response.ok) { return response.json(); } 
      else { return Promise.reject(response); }
    })
    .then(courseList => this.setState({ courses: courseList}))
  }

  render() {
    return (
      <div className="Home">
        <h1 class = "pad">Scheduler Application </h1>
        <Dropdown/>
        <Search />

       <SearchBar />
            <ul>
            </ul>

      </div>
    );
  }
}

export default Home;
