import React, { Component } from "react";
import "../pages/Home.css";
import Search from '../component/Search';
import SearchBar from '../component/SearchBar';
import Dropdown from '../component/Dropdown';

const userId = 2;

function equals(l1, l2) {
  if (l1.length != l2.length) {
    return false;
  }
  return l1[0] == l2[0] && l1[1] == l2[1];
}

function includes(list, listoflist) {
  for (let l of listoflist) {
    if(equals(list, l)) {
      return true;
    }
  }
  return false;
}

function courseTime(course) {
  if (course === undefined) {
    return "ERROR";
  } else {
    let meetDays = ["M", "T", "W", "R", "F"].filter(day => course.time[day].length > 0);
    let meetTimes = [];
    meetDays.forEach(day => {
      if (!includes(course.time[day], meetTimes)) {
        meetTimes.push(course.time[day]);
      }
    });
  }
}

function parseTime(ts) {
  if (ts == "") {
    return -1;
  }
  components = ts.split(" ");
  time = components[0].split(":");
  hr = parseInt(time[0])
  hr += (time.length > 1) ? parseInt(time[1]) / 60.0 : 0
  return hr + ((components[1] == "AM") ? 0 : 12);
}

function overlap(course1, course2) {
  for (let day in course1.time) {
    if (course1.time[day].length == 0 || course2.time[day].length == 0) {
      continue;
    } else {
      t1 = [parseTime(course1.time[day][0]), parseTime(course1.time[day][1])];
      t2 = [parseTime(course2.time[day][0]), parseTime(course2.time[day][1])];
      if(t1[0] > t2[1] || t2[0] > t1[1]) {
        continue;
      } else {
        return true;
      }
    }
  }
  return false;
}

class Schedule {
  constructor() {
    this.name = "";
    this.term = "";
    this.courses = [];
    this.savedStatus = false;
  }

  addCourse(course) {
    if (this.term == "") {
      this.term = course.term;
    }
    if (course.term != this.term) {
      throw `Based on other classes you have added, 
      this schedules is for the ${(this.term == "F20") ? "fall" : "spring"} 
      but the section of ${course.title} you selected is not offered in that term.`;
    } else if(this.courses.filter(addedCourse => addedCourse.title == course.title).length != 0) {
      throw  `You already have a section of ${course.title} in your schedule!`;
    } else {
      for (let addedCourse of this.courses) {
        if (overlap(addedCourse, course)) {
          throw `${course.title} overlaps with ${addedCourse.title}, please remove one of those`;
        }
      }
      this.courses.push(course);
      this.savedStatus = false;
    }
  }

  rename(newName) {
    this.name = newName;
    this.savedStatus = false;
  }

  removeCourse(title) {
    this.courses = this.courses.map(course => course.title != title);
    this.savedStatus = false;
  }

  save() {
    // TODO communicate with backend
    this.savedStatus = true;
  }
}

class Home extends Component {
  // Create a default state of this component
  constructor() {
    super();
    this.state = {
      schedule: null,
      schedules: [],
      courses: [],
      term: "",
      title: "",
      prof: "",
      dept: "",
      min: 0,
      max: 488,
      credits: -1,
      id: 2
    };
    this.updatefilters = this.updatefilters.bind(this);
    this.searchedCourse = this.searchedCourse.bind(this);
    this.addCourse = this.addCourse.bind(this);
    this.getSchedule = this.getSchedule.bind(this)
  }

  componentDidMount() {
    fetch("http://localhost:8080/schedules?id=" + this.state.id)
    .then(response => {
      if (response.ok) { return response.json(); }
      else { return Promise.reject(response); }
    }).then(data => this.setState({ schedules: data[0].schedules.map(sched => sched.name) }));
    console.log(this);
    fetch('http://localhost:8080/courses')
    .then(response => {
      if (response.ok) { return response.json(); } 
      else { return Promise.reject(response); }
    }).then(courseList => this.setState({ courses: courseList}));
  }

  updatefilters(input) {
    if (input.target.name == "term") {
      this.setState({ term: (input.target.value == "Fall") ? "F20": "S21" })
    } else if (input.target.name == "course search") {
      this.setState({ title: input.target.value.toLowerCase() })
    } else if (input.target.name == "prof search") {
      this.setState({ prof: input.target.value.toLowerCase() })
    } else if (input.target.name == "min") {
      this.setState({ min: (input.target.value < 0) ? 0 : Math.floor(input.target.value )})
    } else if (input.target.name == "max") {
      this.setState({ max: (input.target.value > 488) ? 488 : input.target.value })
    } else if (input.target.name == "dept") {
      this.setState({ dept: input.target.value.toLowerCase() })
    } else if (input.target.name == "credits") {
      this.setState({ credits: Math.floor(input.target.value)})
    }
  }

  searchedCourse(course) {
    let term = course.term == this.state.term || this.state.term == "";
    let title = course.title.toLowerCase().includes(this.state.title);
    let prof = course.prof.toLowerCase().includes(this.state.prof);
    let num = (
      () => {
        let code = course.code.split(" ")[1];
        if (this.state.min <= this.state.max) {
          return this.state.min <= code && this.state.max >= code;
        } else {
          return this.state.max <= code && this.state.min >= code;
        }
      }
    )()
    let dept = course.code.split(" ")[0].toLowerCase().includes(this.state.dept)
    let credits = course.credits == this.state.credits || this.state.credits == -1;
    return term && title && prof && num && dept && credits;    
  }

  addCourse(event) {
    let course = this.state.courses[parseInt(event.target.id)]
    try {
      this.state.schedule.addCourse(course);
    } catch(error) {
      // TODO actually handle error
      console.log(error) // TODO remove this when error is properly handled
    }
    console.log(this.state.schedule)
  }

  getSchedule(event) {
    // TODO query backend
  }

  render() {
    let filtCourses = this.state.courses.filter(course => this.searchedCourse(course));
    return (
      <div className="Home">
        <h1 class = "pad">Scheduler Application </h1>
        <div>
          <select onChange = { this.getSchedule }>
            <option>Select a schedule</option>
            {
              this.state.schedules.map(sched => {
                return <option>{sched.name}</option>
              })
            }
          </select>
        </div>
        <form action="/" method="get">
          <div>
            <label for="course-search">Search Courses: </label>
            <input 
            type = "text"
            id = "course-search"
            name = "course search"
            onChange = { this.updatefilters }/>
          </div>
          <div>
            <label for="prof-search">Search for a Professor: </label>
            <input 
            type = "text"
            id = "prof-search"
            name = "prof search"
            onChange = { this.updatefilters }/>
          </div>
          <div>
            <div>
                <input type = "radio" name = "term" value = "Fall" onInput={ this.updatefilters }></input>
                <label>Fall</label>
            </div>
            <div>
                <input type = "radio" name = "term" value = "Spring" onInput={ this.updatefilters }></input>
                <label>Spring</label>
            </div>
          </div>
          <div>
            <div>
              <label>Course Code Minimum: </label>
              <input type = "number" name = "min" min = "0" max = "488" onInput= { this.updatefilters }></input>
            </div>
            <div>
              <label>Course Code Maximum: </label>
              <input type = "number" name = "max" min = "0" max = "488" onInput= { this.updatefilters }></input>
            </div>
          </div>
          <div>
            <label>Department: </label>
            <input type = "text" name = "dept" onChange= { this.updatefilters }></input>
          </div>
          <div>
            <label>Credits: </label>
            <input type = "number" name = "credits" min = "0" max = "4" onInput= { this.updatefilters }></input>
          </div>
        </form>
        <table>
          <thead>
            <tr>
              <td></td>
              <td>Term</td>
              <td>Code</td>
              <td>Section</td>
              <td>Title</td>
              <td>Professor</td>
              <td>Credits</td>
              <td>Time</td>
            </tr>
          </thead>
          <tbody>
            {filtCourses.map(course => {
              return (
              <tr>
                <td><button id = {this.state.courses.indexOf(course) } onClick={ this.addCourse }>Add</button></td>
                <td>{(course.term == "F20") ? "Fall" : "Spring"}</td>
                <td>{course.code.split(" ").slice(0,2).join(" ")}</td>
                <td>{course.code.split(" ")[2]}</td>
                <td>{course.title}</td>
                <td>{course.prof}</td>
                <td>{course.credits}</td>
                <td>{courseTime(course)}</td>
              </tr>
              )
            })}
          </tbody>
        </table>
      </div>
    );
  }
}

export default Home;
